{
  "design_type": "cluster_rct",
  "unit_of_randomization_canonical": "urban district (woreda)",
  "is_clustered": true,
  "analysis_unit_canonical": null,
  "primary_outcomes_dedup": [],
  "arms": [
    {
      "arm_id": "arm1",
      "name": "Urban Productive Safety Net Program (UPSNP)",
      "role": "treatment",
      "description": "Comprehensive social protection program for urban households; beneficiaries are assigned to either a public works component or, if unable to work, an unconditional direct support transfer.",
      "evidence_quote_ids": [
        "eq1"
      ]
    },
    {
      "arm_id": "arm2",
      "name": "Public works (PW) component (within-program assignment)",
      "role": "experimental",
      "description": "Within UPSNP, beneficiaries engage in a public works program.",
      "evidence_quote_ids": [
        "eq1"
      ]
    },
